import styled from "styled-components";

export const Container = styled.div`
    width: 100%;
    height: 100%;
    position: absolute;
    top: 0;
    background-color: rgba(249, 246, 240, 0.70);
    z-index: 3;

    form {
        position: absolute;
        background-color: rgba(249, 246, 240);
        top: 50%;
        left: 50%;
        transform: translate(-50%, -50%);
        max-width: 43.75rem;
        width: 100%;
        height: 62.5rem;
        text-align: center;

        input[type='text'] {
            width: 80%;
            height: 5.375rem;
        }
    }
`;
