import styled from 'styled-components';

export const Container = styled.div`
    position: absolute;
    top: 0;
    left: 0;
    height: 100vh;
    max-width: 25rem;
    width: 100%;
    background-color: #F9F6F0;

    .head {
        width: 100%;
        height: 5.625rem;
        background-color: #161616;
        text-align: right;
        padding-top: 1.4rem;

        .close {
            margin: 0 1.2rem;
        }
    }

    ul {    
        width: 100%;

        .menu {
            color: #444c54;
            padding: 1rem;
            background-color: rgba(239, 236, 230);
            display: flex;
            justify-content: space-between;

            @keyframes arrow { //Animação para seta durante hover na opção de menu
                0%   {top: 0;}
                25%  {top: -4px;}
                50%  {top: 0;}
                100% {margin-top: 4px;}
            }

            :hover {
                .arrow {
                    position: absolute;
                    animation-name: arrow;
                    animation-duration: 2s;
                }
            }

            ul.subMenu {
                display: none;
            }
        }

        li {
            width: 100%;

            a {
                display: inline-block;
                width: 100%;
                height: 100%;
                text-decoration: none;
                color: #444c54;
                padding: 1rem;
                :hover {
                    background-color: rgba(239, 236, 230);
                }
            }
        }
    }
`;
