import { Container } from './styles'
import {useDynimicityContext} from '../../Context/useDynimicityContext'
import { useState } from 'react';

const Sidebar = () => {

    const {sizeSidebar, displaySidebar, sidebarShow} = useDynimicityContext();

    const [sideType, setSideType] = useState(["block", "none"])
    const [isLogado] = useState(true) 
    // Essa variável acima é a que precisa ser alterada (recebida do banco). Supostamente o algoritmo abaixo verifica se está locado e altera o conteúdo do sidebar

    const alterarSideType = () => (isLogado && (sideType[0] === "block") ? setSideType(["none", "block"]) : setSideType(["block", "none"]))

    return (
        <Container>
            <div className="smoothTransition" style={{width: sizeSidebar, display: displaySidebar}}>
                <div className="wrapper">
                    <div className="header">

                        <p>Adicionar aos favoritos</p>

                        <div className="close" onClick={() => {
                                sidebarShow("30%")
                            }}>

                            <svg width="34" height="34" viewBox="0 0 36 34" fill="none" xmlns="http://www.w3.org/2000/svg">
                                <line x1="2.70711" y1="1.29289" x2="34.7071" y2="33.2929" stroke="#F9F6F0" strokeWidth="2"/>
                                <line x1="1.29289" y1="33.2929" x2="33.2929" y2="1.29291" stroke="#F9F6F0" strokeWidth="2"/>
                            </svg>
                        </div>
                    </div>

                    <div className="deslogado" style={{display: sideType[0]}}>
                        <div className="boxAlert">
                            <p>Logue-se para poder adicionar seus pratos favoritos!</p>
                        </div>
                    </div>

                    <div className="logado" style={{display: sideType[1]}}>
                        <div className="boxAlert">
                            <p>Logue-se para poder adicionar seus pratos favoritos!</p>
                        </div>
                    </div>
                </div>
            </div>
        </Container>
    )
}

export default Sidebar
